DEFINE {{retrieval_modes}}: Integer = 3
DEFINE {{team_members}}: Integer = 5
DEFINE {{java_developers}}: Integer = 2
DEFINE {{python_developers}}: Integer = 3
DEFINE {{project_duration_weeks}}: Integer = 8
DEFINE {{budget}}: Integer = 500000
DEFINE {{tech_stack}}: List = ['LangChain', 'Milvus', 'FastAPI']
DEFINE {{multi_turn_dialogue}}: Boolean = True
DEFINE {{history_context_rounds}}: Integer = 20
DEFINE {{deployment_environment}}: String = Kubernetes集群
DEFINE {{architecture}}: String = 微服务架构
DEFINE {{service_count}}: Integer = 10
DEFINE {{monitoring_system}}: List = ['Prometheus', 'Elasticsearch', 'Logstash', 'Kibana']
DEFINE {{bilingual_support}}: Boolean = True
DEFINE {{response_time_limit}}: Integer = 2
DEFINE {{rag_app}}: Any
DEFINE {{team_structure}}: Any
DEFINE {{project_plan}}: Any
DEFINE {{tech_stack_config}}: Any
DEFINE {{dialogue_system}}: Any
DEFINE {{deployment_plan}}: Any
DEFINE {{monitoring_setup}}: Any
DEFINE {{language_support}}: Any
DEFINE {{performance_constraints}}: Any
{{rag_app}} = CALL design_rag_application(
    retrieval_modes={{retrieval_modes}},
    llm_backend=True,
    complex_chain=True
)
{{team_structure}} = CALL define_team_structure(
    total_members={{team_members}},
    java={{java_developers}},
    python={{python_developers}}
)
{{project_plan}} = CALL create_project_plan(
    duration_weeks={{project_duration_weeks}},
    budget={{budget}}
)
{{tech_stack_config}} = CALL configure_tech_stack(
    stack={{tech_stack}}
)
{{dialogue_system}} = CALL setup_dialogue_system(
    multi_turn={{multi_turn_dialogue}},
    history_rounds={{history_context_rounds}}
)
{{deployment_plan}} = CALL plan_deployment(
    environment={{deployment_environment}},
    architecture={{architecture}},
    service_count={{service_count}}
)
{{monitoring_setup}} = CALL integrate_monitoring_and_logging(
    systems={{monitoring_system}}
)
{{language_support}} = CALL enable_bilingual_support(
    bilingual={{bilingual_support}}
)
{{performance_constraints}} = CALL set_performance_constraints(
    response_time={{response_time_limit}}
)
RETURN {{rag_app}}
